function split(inputstr, sep)
   if sep == nil then
      sep = "%s"
   end
   local t={}
   for str in string.gmatch(inputstr, "([^"..sep.."]+)") do
      table.insert(t, str)
   end
   return t
end

local hey = ""
local test = ""
for i,v in pairs(split(hey,"/")) do
ph = string.char(v)
test = test..ph
end

return test
